import React, { useEffect } from "react";
import { filterByCat, getDoctorData } from "../Redux/Action";
import {useNavigate} from "react-router-dom"
import { useDispatch, useSelector } from "react-redux";



const genres = ["", "", "Thriller", "Drama", "Documentary", "Children"];

export const HomePage = () => {
  // to get all movies list on component mounts
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const state = useSelector((state) => state);
  
  useEffect(() => {
  
    dispatch(getDoctorData());
  }, [dispatch]);
  


  const handleFilter = (e) => {
  

    dispatch(filterByCat(e.target.value));
  };

  const handleCardClick = (id) => {
    navigate(`/doctors/${id}`);
  };
  return (
    <>
     
      <h2>Movies</h2>
      <select onChange={handleFilter}>
        {
          /* map through the filter  */
          genres.map((el, index) => {
            return <option key={index}>{el}</option>;
          })
        }
      </select>
      <div  className="movies-list">
        {
          /* map throught th movie list and display the results */
          state.filteredData.map((el) => (
            <div  onClick={()=>handleCardClick(el.id)}>
              {/* <img src={el.img} alt="" /> */}
              <h4>{el.name}</h4>
              {/* <p>{el.id}</p> */}
            </div>
          ))
        }
      </div>
    </>
  );
};
